import {React, Component} from "react";
import "./JokeSorter.css";

class SortButton extends Component {
    constructor(props) {
        super(props);
        this.state = {
            active: props.active
        }
        this.turnOn = () => {
            this.setState({active: true});     
        }
        this.turnOff = () => {
            this.setState({active: false});
        }
    }

    render() {
        const {name} = this.props;
        const classNames = (this.state.active ? "button-sort active" : "button-sort");
        return (
            <button 
                className={classNames}
            >
                {name}
            </button>
        )
    }    
}

class JokeSorter extends Component {
    constructor (props) {
        super(props);
        this.state = {
            buttonsStates: [false, false, false, false, false, false]
        }
    }

    renderButton(i) {
        switch (i) {
        case 0: return <SortButton name="Новые" link="/" active={this.state.buttonsStates[0]} />;
        case 1: return <SortButton name="1 час" link="/" active={this.state.buttonsStates[1]} />;
        case 2: return <SortButton name="1 день" link="/" active={this.state.buttonsStates[2]} />;
        case 3: return <SortButton name="1 неделя" link="/" active={this.state.buttonsStates[3]} />;
        case 4: return <SortButton name="1 месяц" link="/" active={this.state.buttonsStates[4]} />; 
        case 5: return <SortButton name="Всё время" link="/" active={this.state.buttonsStates[5]} />; 
        default: return <></>
        }
    }
    render() {
        return (
            <div className="sorter" onClick={(event)=>{
                    console.log(event.target);
                    console.log(event.target.textContent);
                    let id = 0;
                    switch (event.target.textContent) {
                    case "1 час": id = 1; break;
                    case "1 день": id = 2; break;
                    case "1 неделя": id = 3; break;
                    case "1 месяц": id = 4; break;
                    case "Всё время": id = 5; break;
                    default: break;
                    }
                    console.log(id);
                    this.state.buttonsStates.forEach((element, idx) => {
                        if (idx === id)
                            element = true;
                        else 
                            element = false;
                        this.renderButton(idx);
                    });
                }}>
                {this.renderButton(0)}

                <div style={{marginTop: "15px", marginLeft: "15px", marginRight: "15px"}}>Лучшее за:</div>

                {this.renderButton(1)}
                {this.renderButton(2)}
                {this.renderButton(3)}
                {this.renderButton(4)}
                {this.renderButton(5)}

            </div>
        )
    }
}

export default JokeSorter;
