                href="/"
            >
                Новые
            </a>
        )
    }    
}

class JokeSorter extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            value: null,
        };
    }

    render() {
        const Label = ["new", "hour", "day", "week", "month", "all-time"];
        let activeButton = "new";
        return (
            <div className="sorter">
                <a 
                    onClick={()=>{activeButton = Label[0]}}
                    className={activeButton === Label[0] ? "button-sort active" : "button-sort"}
                    href="/"
                >
                    Новые
                </a>
                Лучшее за
                <a 
                    onClick={()=>{activeButton = Label[1]}}
                    className={activeButton === Label[1] ? "button-sort active" : "button-sort"}
                    href="/"
                >
                    1 час
                </a>
                <a 
                    onClick={()=>{activeButton = Label[2]}}
                    className={activeButton === Label[2] ? "button-sort active" : "button-sort"}
                    href="/"
                >
                    1 день
                </a>
                <a 
                    onClick={()=>{activeButton = Label[3]}}
                    className={activeButton === Label[3] ? "button-sort active" : "button-sort"}
                    href="/"
                >
                    1 неделя
                </a>
                <a 
                    onClick={()=>{activeButton = Label[4]}}
                    className={activeButton === Label[4] ? "button-sort active" : "button-sort"}
                    href="/"
                >
                    1 месяц
                </a>
                <a 
                    onClick={()=>{activeButton = Label[5]}}
                    className={activeButton === Label[5] ? "button-sort active" : "button-sort"}
                    href="/"
                >
                    Всё время
                </a>

            </div>
        )
    }
}

export default JokeSorter;
