import React from "react";
import "../styles/JokeSorter.css";

class SortButton extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            className: "button-sort"
        }
    }

    render() {
        return (
            <a 
                onClick={()=>{this.setState(state => ({className: "button-sort active"}))}}
                className={this.state.className}
                href={this.props.link}
            >
                {this.props.name}
            </a>
        )
    }    
}

class JokeSorter extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            value: null,
        };
    }

    render() {
        return (
            <div className="sorter">
                <SortButton name="Новые" link="/" />
                Лучшее за

                <SortButton name="1 час" link="/" />

                <SortButton name="1 день" link="/" />

                <SortButton name="1 неделя" link="/" />

                <SortButton name="1 месяц" link="/" />

                <SortButton name="Всё время" link="/" />

            </div>
        )
    }
}

export default JokeSorter;
