import {React, Component} from "react";
import "../styles/JokeSorter.css";

class SortButton extends Component {
    constructor(props) {
        super(props);
        this.state = {
            active: false 
        }
    }

    turnOn() {
        this.setState({active: true});     
    }

    turnOff() {
        this.setState({active: false});
    }

    render() {
        const {name} = this.props;
        const classNames = (this.state.active ? "button-sort active" : "button-sort");
        return (
            <button 
                className={classNames}
            >
                {name}
            </button>
        )
    }    
}

class JokeSorter extends Component {
    constructor (props) {
        super(props);
        this.state = {
            sortButtons: [
                <SortButton name="Новые" link="/" />,
                <SortButton name="1 час" link="/" />, 
