import {React, Component} from "react";
import "./JokeSorter.css";


class SortButton extends Component {
    constructor(props) {
        super(props);
        this.state = {
            active: false
        };
        this.turnOn = this.turnOn.bind(this);
        this.turnOff = this.turnOff.bind(this);
    }

    turnOn() {
        this.setState(() => ({
            active: true
        }));
    }

    turnOff() {
        this.setState(() => ({
            active: false
        }))
    }

    render() { 
        const {name, link} = this.props;
        const {active} = this.state;
        const className = "button-sort" + (active ? " active" : "");
        return (
            <a
                onClick={(actv)=>(actv?this.turnOn():this.turnOff())} 
                className={className}
                href={link}> 
                {name}
            </a>
        )
    }
}

class JokeSorter extends Component {
    constructor (props) {
        super(props);
        this.state = {
            sortButtons: [
                <SortButton name="Новые" link="/" />,
                <SortButton name="1 час" link="/" />,
                <SortButton name="1 день" link="/" />,
                <SortButton name="1 неделя" link="/" />,
                <SortButton name="1 месяц" link="/" />, 
                <SortButton name="Всё время" link="/" />,
            ]
        }
    }

    renderButton(i) {
        return this.state.sortButtons[i];
    }
    render() {
        return (
            <div className="sorter" onClick={(event)=>{
                    event.preventDefault();
                    console.log(event.target);
                    console.log(event.target.textContent);
                    let id = 0;
                    switch (event.target.textContent) {
                    case "1 час": id = 1; break;
                    case "1 день": id = 2; break;
                    case "1 неделя": id = 3; break;
                    case "1 месяц": id = 4; break;
                    case "Всё время": id = 5; break;
                    default: break;
                    }
                    for (let i = 0; i < 6; ++i) {
                        if (id === i) {
                            this.state.sortButtons[i].onClick(true);
                        } else {
                            this.state.sortButtons[i].onClick(false);
                        }
                    }
                }}>
                {this.renderButton(0)}

                <div style={{marginTop: "15px", marginLeft: "15px", marginRight: "15px"}}>Лучшее за:</div>

                {this.renderButton(1)}
                {this.renderButton(2)}
                {this.renderButton(3)}
                {this.renderButton(4)}
                {this.renderButton(5)}

            </div>
        )
    }
}

export default JokeSorter;
