import {React, Component} from "react";
import "./JokeSorter.css";


class SortButton extends Component {
    constructor(props) {
        super(props);
        this.state = {
            active: false
        };
        this.turnOn = this.turnOn.bind(this);
        this.turnOff = this.turnOff.bind(this);
    }

    turnOn() {
        this.setState(() => ({
            active: true
        }));
    }

    turnOff() {
        this.setState(() => ({
            active: false
        }))
    }

    render() { 
        const {name, link} = this.props;
        const active = this.props.state;
        const className = "button-sort" + (active ? " active" : "");
        return (
            <a className={className}
               onClick={this.turnOn}
               href={link}>
                {name}
            </a>
        )
    }
}

class JokeSorter extends Component {
    constructor (props) {
        super(props);
        this.state = {
            buttonsStates: [false, false, false, false, false, false]
        }
    }

    renderButton(i) {
        switch (i) {
        case 0: return <SortButton name="Новые" clName={(this.state.buttonsStates[0] ? "button-sort active" : "button-sort")} link="/" />;
        case 1: return <SortButton name="1 час" clName={(this.state.buttonsStates[1] ? "button-sort active" : "button-sort")} link="/" />;
        case 2: return <SortButton name="1 день" clName={(this.state.buttonsStates[2] ? "button-sort active" : "button-sort")} link="/" />;
        case 3: return <SortButton name="1 неделя" clName={(this.state.buttonsStates[3] ? "button-sort active" : "button-sort")} link="/" />;
        case 4: return <SortButton name="1 месяц" clName={(this.state.buttonsStates[4] ? "button-sort active" : "button-sort")} link="/" />; 
        case 5: return <SortButton name="Всё время" clName={(this.state.buttonsStates[5] ? "button-sort active" : "button-sort")} link="/" />; 
        default: return <></>
        }
    }
    render() {
        return (
            <div className="sorter" onClick={(event)=>{
                    event.preventDefault();
                    console.log(event.target);
                    console.log(event.target.textContent);
                    let id = 0;
                    switch (event.target.textContent) {
                    case "1 час": id = 1; break;
                    case "1 день": id = 2; break;
                    case "1 неделя": id = 3; break;
                    case "1 месяц": id = 4; break;
                    case "Всё время": id = 5; break;
                    default: break;
                    }
                    console.log(id);
                }}>
                {this.renderButton(0)}

                <div style={{marginTop: "15px", marginLeft: "15px", marginRight: "15px"}}>Лучшее за:</div>

                {this.renderButton(1)}
                {this.renderButton(2)}
                {this.renderButton(3)}
                {this.renderButton(4)}
                {this.renderButton(5)}

            </div>
        )
    }
}

export default JokeSorter;
