import {React, Component} from "react";
import "./JokeSorter.css";


const SortButton = ({name, clName, link}) => { 
    return (
        <a className={clName} href={link}>
            {name}
        </a>
    )   
}
class JokeSorter extends Component {
    constructor (props) {
        super(props);
        this.state = {
            buttonsStates: [false, false, false, false, false, false]
        }
    }

    renderButton(i) {
        switch (i) {
        case 0: return <SortButton name="Новые" clName={("button-sort active" ? this.state.buttonsStates[0] : "button-sort")} link="/" />;
        case 1: return <SortButton name="1 час" clName={("button-sort active" ? this.state.buttonsStates[1] : "button-sort")} link="/" />;
        case 2: return <SortButton name="1 день" clName={("button-sort active" ? this.state.buttonsStates[2] : "button-sort")} link="/" />;
        case 3: return <SortButton name="1 неделя" clName={("button-sort active" ? this.state.buttonsStates[3] : "button-sort")} link="/" />;
        case 4: return <SortButton name="1 месяц" clName={("button-sort active" ? this.state.buttonsStates[4] : "button-sort")} link="/" />; 
        case 5: return <SortButton name="Всё время" clName={("button-sort active" ? this.state.buttonsStates[5] : "button-sort")} link="/" />; 
        default: return <></>
        }
    }
    render() {
        return (
            <div className="sorter" onClick={(event)=>{
                    event.preventDefault();
                    console.log(event.target);
                    console.log(event.target.textContent);
                    let id = 0;
                    switch (event.target.textContent) {
                    case "1 час": id = 1; break;
                    case "1 день": id = 2; break;
                    case "1 неделя": id = 3; break;
                    case "1 месяц": id = 4; break;
                    case "Всё время": id = 5; break;
                    default: break;
                    }
                    console.log(id);
                    this.state.buttonsStates.forEach((element, idx) => {
                        if (idx === id)
                            element = true;
                        else 
                            element = false;
                    });
                }}>
                {this.renderButton(0)}

                <div style={{marginTop: "15px", marginLeft: "15px", marginRight: "15px"}}>Лучшее за:</div>

                {this.renderButton(1)}
                {this.renderButton(2)}
                {this.renderButton(3)}
                {this.renderButton(4)}
                {this.renderButton(5)}

            </div>
        )
    }
}

export default JokeSorter;
