import {React, Component} from "react";
import "../styles/JokeSorter.css";

class SortButton extends Component {
    constructor(props) {
        super(props);
        this.state = {
            active: false 
        }
    }

    changeState() {
        this.setState({active: !this.state.active});     
    }

    render() {
        const {name, link} = this.props;
        return (
            <a 
                onClick={this.changeState}
                className={(this.state.active ? "button-sort active" : "button-sort")}
                href={link}
            >
                {name}
            </a>
        )
    }    
}

class JokeSorter extends Component {
    constructor (props) {
        super(props);
        this.state = {
            sortButtons: props.btn
        }
    }

    renderButton(i) {
        switch (i) {
        case 1: return <SortButton name="Новые" link="/" />; 
        case 2: return <SortButton name="1 час" link="/" />; 
        case 3: return <SortButton name="1 день" link="/" />;
        case 4: return <SortButton name="1 неделя" link="/" />;
        case 5: return <SortButton name="1 месяц" link="/" />;
        case 6: return <SortButton name="Всё время" link="/" />;
        default:return <></>
        }
    }

    render() {
        return (
            <div className="sorter">
                {this.renderButton(1)}

                <div style={{marginTop: "15px", marginLeft: "15px"}}>Лучшее за</div>

                {this.renderButton(2)}
                {this.renderButton(3)}
                {this.renderButton(4)}
                {this.renderButton(5)}
                {this.renderButton(6)}

            </div>
        )
    }
}

export default JokeSorter;
