import {React} from "react";
import "../styles/JokeSorter.css";

class SortButton extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            active: false 
        }
    }

    changeState() {
        this.setState(state => ({active: !this.state.active}));     
    }

    render() {
        return (
            <a 
                onClick={this.changeState()}
                className={()=>(this.state.active ? "button-sort active" : "button-sort")}
                href={this.props.link}
            >
                {this.props.name}
            </a>
        )
    }    
}

