import { useNavigate } from "react-router-dom"
import { useCreateJokeMutation } from "../../services/Joke";
import { useState } from "react";
import './CreateJoke.css';

const CreateJoke = () => {
    const navigate = useNavigate();

    const [createJoke] = useCreateJokeMutation();
    const [modalVisible, setModalVisible] = useState();
    let headerText, 
        descriptionText;
    const userID = localStorage.getItem('userID');

    const onClick = async (headerText, descriptionText) => {
        try {
            await createJoke({
                header: headerText,
                description: descriptionText,
                author_id: userID,
            });
        } catch (error) {
            throw error;
        }
        if (modalVisible === 'on')
            setModalVisible('off');
        navigate(-1);
    }

    return (
        <div className="modal-window">
            <input type="text" className="new-header" placeholder="Заголовок вашей шутки" value={headerText}/>
            <input type="text" className="new-description" placeholder="Текст вашей шутки" value={descriptionText}/>
            <button className="create-button" onClick={onClick}>
                Создать
            </button>
        </div>
    )
}

export default CreateJoke;
